// src/pages/CastVote.jsx

import React, { useEffect, useState } from 'react';
import axios from 'axios';
import './CastVote.css'; // optional
import { useNavigate } from 'react-router-dom';

const CastVote = () => {
  const [candidates, setCandidates] = useState([]);
  const [selectedChoice, setSelectedChoice] = useState('');
  const [message, setMessage] = useState('');
  const [slotStatus, setSlotStatus] = useState('');
  const navigate = useNavigate();

  const token = localStorage.getItem('voteyatra_token');

  useEffect(() => {
    if (!token) {
      navigate('/login');
      return;
    }

    const fetchData = async () => {
      try {
        // üîπ Check user's slot and vote status
        const slotRes = await axios.get('http://localhost:3000/vote/user-slot', {
          headers: { Authorization: `Bearer ${token}` },
        });

        const { hasVoted, slot } = slotRes.data;

        if (hasVoted) {
          setSlotStatus('üü¢ You have already voted.');
          return;
        }

        if (!slot) {
          setSlotStatus('‚ö†Ô∏è Please book a slot first.');
          return;
        }

        // üîπ Parse time
        const now = new Date();
        const [start, end] = slot.split(' - ');
        const today = now.toDateString();

        const parseToDate = (timeStr) => {
          const [t, ampm] = timeStr.split(' ');
          let [h, m] = t.split(':');
          h = parseInt(h);
          m = parseInt(m);
          if (ampm === 'PM' && h !== 12) h += 12;
          if (ampm === 'AM' && h === 12) h = 0;
          return new Date(`${today} ${h}:${m}`);
        };

        const startTime = parseToDate(start);
        const endTime = parseToDate(end);

        if (now < startTime) {
          setSlotStatus(`‚è≥ Voting starts at ${start}. Please come back later.`);
          return;
        }

        if (now > endTime) {
          setSlotStatus(`‚õî Your voting slot (${slot}) has expired.`);
          return;
        }

        setSlotStatus(`‚úÖ You can vote now. Slot: ${slot}`);

        // üîπ Fetch candidates
        const candidateRes = await axios.get('http://localhost:3000/candidates', {
          headers: { Authorization: `Bearer ${token}` },
        });

        setCandidates(candidateRes.data);
      } catch (err) {
        setSlotStatus('Error: ' + (err.response?.data?.message || 'Server error'));
      }
    };

    fetchData();
  }, [navigate, token]);

  const handleVote = async () => {
    if (!selectedChoice) {
      alert('Please select a candidate');
      return;
    }

    try {
      const res = await axios.post(
        'http://localhost:3000/vote/cast-vote',
        { choice: selectedChoice },
        { headers: { Authorization: `Bearer ${token}` } }
      );
      setMessage(res.data.message);
    } catch (err) {
      setMessage(err.response?.data?.message || 'Failed to cast vote');
    }
  };

  return (
    <div className="cast-vote-container">
      <h2>üó≥Ô∏è Cast Your Vote</h2>
      <p className="slot-status">{slotStatus}</p>

      {slotStatus.startsWith('‚úÖ') && (
        <>
          <ul className="candidate-list">
            {candidates.map((c) => (
              <li key={c._id}>
                <label>
                  <input
                    type="radio"
                    name="candidate"
                    value={c._id}
                    onChange={() => setSelectedChoice(c._id)}
                  />
                  {c.name} ({c.party})
                </label>
              </li>
            ))}
          </ul>
          <button onClick={handleVote} className="vote-btn">Cast Vote</button>
        </>
      )}

      {message && <p className="vote-message">{message}</p>}
    </div>
  );
};

export default CastVote;
