import React, { useEffect, useState } from 'react';
import { Link, useNavigate } from 'react-router-dom';
import emblem from '../assets/candidates/emblem.jpg';
import './CandidateList.css';
import modiImg from '../assets/candidates/narendra_modi.jpg';
import rahulImg from '../assets/candidates/Rahul_Gandhi.jpg';
import kejriwalImg from '../assets/candidates/Arvind_Kejriwal.jpg';
import owaisiImg from '../assets/candidates/Asaduddin_Owaisi.jpg';
import './Announcement.css';

function Announcement() {
  const [announcements, setAnnouncements] = useState([]);
  const [newAnnouncement, setNewAnnouncement] = useState('');
  const [isAdmin, setIsAdmin] = useState(false);
  const [message, setMessage] = useState('');

  const token = localStorage.getItem('voteyatra_token');

  useEffect(() => {
    fetchAnnouncements();

    // Decode token to check if user is admin (or use user info stored locally)
    const userRole = localStorage.getItem('voteyatra_role');
    setIsAdmin(userRole === 'admin');
  }, []);

  const fetchAnnouncements = async () => {
    try {
      const res = await axios.get('http://localhost:3000/vote/announcements');
      setAnnouncements(res.data);
    } catch (err) {
      console.error('Error fetching announcements:', err);
    }
  };

  const handlePost = async () => {
    if (!newAnnouncement.trim()) return;

    try {
      const res = await axios.post(
        'http://localhost:3000/vote/announcements',
        { message: newAnnouncement },
        {
          headers: {
            Authorization: `Bearer ${token}`,
          },
        }
      );
      setMessage(res.data.message);
      setNewAnnouncement('');
      fetchAnnouncements();
    } catch (err) {
      setMessage(err.response?.data?.message || 'Failed to post announcement');
    }
  };

  return (
    <div className="announcement-container">
      {/* ‚úÖ Header Section */}
      <header className="home-header">
        <img src={emblem} alt="Indian Emblem" className="emblem" />

        <Link to="/" className="no-underline">
          <h1 className="home-title">üó≥Ô∏è VoteYatra</h1>
        </Link>

        <div className="header-buttons">
          {loggedInUser ? (
            <>
              <span className="welcome-user">Welcome, {loggedInUser}</span>
              <button
                className="logout-btn"
                onClick={() => {
                  localStorage.removeItem('voteyatra_user');
                  localStorage.removeItem('voteyatra_token');
                  setLoggedInUser(null);
                  navigate('/');
                }}
              >
                Logout
              </button>
            </>
          ) : (
            <>
              <Link to="/register">
                <button className="register-header-btn">Register</button>
              </Link>
              <Link to="/login">
                <button className="login-header-btn">Log in</button>
              </Link>
            </>
          )}
        </div>
      </header>

      <hr className="separator" />
      <h2>üì¢ Announcements</h2>

      {isAdmin && (
        <div className="announcement-form">
          <textarea
            rows="3"
            placeholder="Type a new announcement..."
            value={newAnnouncement}
            onChange={(e) => setNewAnnouncement(e.target.value)}
          />
          <button className="post-btn" onClick={handlePost}>Post Announcement</button>
          {message && <p className="post-status">{message}</p>}
        </div>
      )}

      <div className="announcement-list">
        {announcements.length === 0 ? (
          <p className="no-announcements">No announcements yet.</p>
        ) : (
          announcements.map((item, idx) => (
            <div key={idx} className="announcement-card">
              <p>{item.message}</p>
              <span className="timestamp">
                {new Date(item.createdAt).toLocaleString()}
              </span>
            </div>
          ))
        )}
      </div>
    </div>
  );
}

export default Announcement;
