import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { useNavigate } from 'react-router-dom';

const slotMap = {
  1: '9:00 AM - 10:00 AM',
  2: '10:00 AM - 11:00 AM',
  3: '11:00 AM - 12:00 PM',
  4: '12:00 PM - 1:00 PM',
  5: '1:00 PM - 2:00 PM',
  6: '2:00 PM - 3:00 PM',
  7: '3:00 PM - 4:00 PM',
  8: '4:00 PM - 5:00 PM',
  9: '5:00 PM - 6:00 PM',
};

function BookSlot() {
  const [selectedSlot, setSelectedSlot] = useState('');
  const [message, setMessage] = useState('');
  const navigate = useNavigate();

  useEffect(() => {
    const token = localStorage.getItem('voteyatra_token');
    if (!token) {
      alert('Please log in first');
      navigate('/login');
    }
  }, []);

  const handleSubmit = async (e) => {
    e.preventDefault();
    const token = localStorage.getItem('voteyatra_token');

    try {
      const res = await axios.post(
        'http://localhost:3000/vote/book-slot',
        { slot: Number(selectedSlot) },
        {
          headers: { Authorization: `Bearer ${token}` },
        }
      );
      setMessage(res.data.message);
    } catch (err) {
      setMessage(err.response?.data?.message || 'Error booking slot');
    }
  };

  return (
    <div style={{ padding: '20px' }}>
      <h2>ðŸ“… Book Your Voting Slot</h2>
      <form onSubmit={handleSubmit}>
        {Object.entries(slotMap).map(([num, label]) => (
          <div key={num}>
            <label>
              <input
                type="radio"
                name="slot"
                value={num}
                checked={selectedSlot === num}
                onChange={(e) => setSelectedSlot(e.target.value)}
              />
              {label}
            </label>
          </div>
        ))}
        <button type="submit" style={{ marginTop: '10px' }}>Book Slot</button>
      </form>
      {message && <p>{message}</p>}
    </div>
  );
}

export default BookSlot;
