import React, { useEffect, useState } from 'react';
import axios from 'axios';
import './CastVote.css'; // create and style this
import { useNavigate } from 'react-router-dom';

function CastVote() {
  const [candidates, setCandidates] = useState([]);
  const [selectedCandidate, setSelectedCandidate] = useState('');
  const [message, setMessage] = useState('');
  const navigate = useNavigate();
  const token = localStorage.getItem('voteyatra_token');

  useEffect(() => {
    if (!token) {
      alert('Please login to access this page');
      navigate('/login');
    } else {
      fetchCandidates();
    }
  }, []);

  const fetchCandidates = async () => {
    try {
      const res = await axios.get('http://localhost:3000/vote/candidates', {
        headers: {
          Authorization: `Bearer ${token}`,
        },
      });
      setCandidates(res.data);
    } catch (err) {
      setMessage(err.response?.data?.message || 'Failed to fetch candidates');
    }
  };

  const castVote = async () => {
    if (!selectedCandidate) return alert('Please select a candidate');
    try {
      const res = await axios.post(
        'http://localhost:3000/vote/cast-vote',
        { choice: selectedCandidate },
        {
          headers: {
            Authorization: `Bearer ${token}`,
          },
        }
      );
      alert(res.data.message);
      navigate('/'); // redirect after success
    } catch (err) {
      alert(err.response?.data?.message || 'Failed to cast vote');
    }
  };

  return (
    <div className="cast-vote-container">
      <h2>üó≥Ô∏è Cast Your Vote</h2>
      {message && <p className="error">{message}</p>}

      <div className="candidates-list">
        {candidates.map((candidate) => (
          <div
            key={candidate._id}
            className={`candidate-card ${
              selectedCandidate === candidate.name ? 'selected' : ''
            }`}
            onClick={() => setSelectedCandidate(candidate.name)}
          >
            <img src={candidate.symbol} alt={candidate.name} />
            <h4>{candidate.name}</h4>
            <p>{candidate.party}</p>
          </div>
        ))}
      </div>

      <button className="submit-vote-btn" onClick={castVote}>
        Submit Vote
      </button>
    </div>
  );
}

export default CastVote;
