import React, { useState } from 'react';
import axios from 'axios';
import './BookSlot.css';

function BookSlot() {
  const [slot, setSlot] = useState({ start: '', end: '' });
  const [message, setMessage] = useState('');

  const handleSubmit = async (e) => {
    e.preventDefault();
    const token = localStorage.getItem('voteyatra_token');
    if (!token) {
      alert('You must be logged in to book a slot');
      return;
    }

    try {
      const res = await axios.post('http://localhost:3000/vote/book-slot', slot, {
        headers: {
          Authorization: `Bearer ${token}`,
        },
      });
      setMessage(res.data.message);
    } catch (err) {
      setMessage(err.response?.data?.message || 'Failed to book slot');
    }
  };

  const handleChange = (e) => {
    const { name, value } = e.target;
    setSlot((prev) => ({ ...prev, [name]: value }));
  };

  return (
    <div className="book-slot-container">
      <h2>ðŸ“… Book Your Voting Slot</h2>
      <form onSubmit={handleSubmit}>
        <label>
          Slot Start:
          <input type="datetime-local" name="start" value={slot.start} onChange={handleChange} required />
        </label>
        <label>
          Slot End:
          <input type="datetime-local" name="end" value={slot.end} onChange={handleChange} required />
        </label>
        <button type="submit">Book Slot</button>
      </form>
      {message && <p className="status">{message}</p>}
    </div>
  );
}

export default BookSlot;
