import { useEffect } from 'react';
import axios from 'axios';
import { useNavigate } from 'react-router-dom';

const useUserValidation = () => {
  const navigate = useNavigate();

  useEffect(() => {
    const interval = setInterval(async () => {
      const token = localStorage.getItem('voteyatra_token');
      if (!token) return;

      try {
        const res = await axios.get('http://localhost:3000/vote/auth/check-valid-user', {
          headers: { Authorization: `Bearer ${token}` },
        });

        if (!res.data.valid) {
          localStorage.clear();
          alert('Your account was deleted by the admin. Logging you out.');
          window.location.href = '/login';
        }
      } catch (err) {
        if (err.response?.status === 404) {
          localStorage.clear();
          alert('Your account no longer exists. Logging out.');
          window.location.href = '/login';
        }
      }
    }, 10000); // every 10 seconds

    return () => clearInterval(interval);
  }, []);
};

export default useUserValidation;
