import React, { useEffect, useState } from 'react';
import './CandidateList.css';
import modiImg from '../assets/candidates/narendra_modi.jpg';
import rahulImg from '../assets/candidates/Rahul_Gandhi.jpg';
import kejriwalImg from '../assets/candidates/Arvind_Kejriwal.jpg';
import owaisiImg from '../assets/candidates/Asaduddin_Owaisi.jpg';

function CandidateList() {
  const [candidates, setCandidates] = useState([]);

  useEffect(() => {
    setCandidates([
      {
        name: 'Narendra Modi',
        party: 'Bharatiya Janta Party',
        image: modiImg,
        description: 'Narendra Modi is the current Prime Minister...',
        manifesto: 'https://www.bjp.org/bjp-manifesto-2024',
      },
      {
        name: 'Rahul Gandhi',
        party: 'Indian National Congress',
        image: rahulImg,
        description: 'Rahul Gandhi, a leader of the Indian National Congress...',
        manifesto: 'https://inc.in/',
      },
      {
        name: 'Arvind Kejriwal',
        party: 'Aam Aadmi Party',
        image: kejriwalImg,
        description: 'Arvind Kejriwal, founder of the Aam Aadmi Party...',
        manifesto: 'https://aamaadmiparty.org/',
      },
      {
        name: 'Asaduddin Owaisi',
        party: 'AIMIM',
        image: owaisiImg,
        description: 'Asaduddin Owaisi, leader of AIMIM...',
        manifesto: 'https://www.aimim.org/',
      },
    ]);
  }, []);

  return (
    <div className="candidate-list-container">
      <h2>ðŸ“‹ Candidate List</h2>
      {candidates.map((candidate, index) => (
        <div className="candidate-card" key={index}>
          <img src={candidate.image} alt={candidate.name} className="candidate-img" />
          <div className="candidate-info">
            <p className="candidate-name">{candidate.name}</p>
            <p className="candidate-party">{candidate.party}</p>
            <p className="candidate-desc">{candidate.description}</p>
            <a
              href={candidate.manifesto}
              target="_blank"
              rel="noopener noreferrer"
              className="manifesto-link"
            >
              View Manifesto â†’
            </a>
          </div>
        </div>
      ))}
    </div>
  );
}

export default CandidateList;
