// src/pages/CandidateList.jsx

import './CandidateList.css';
import modiImg from '../assets/candidates/narendra_modi.jpg';
import rahulImg from '../assets/candidates/Rahul_Gandhi.jpg';
import kejriwalImg from '../assets/candidates/Arvind_Kejriwal.jpg';
import owaisiImg from '../assets/candidates/Asaduddin_Owaisi.jpg';

function CandidateList() {
  const candidates = [
    {
      name: 'Narendra Modi',
      party: 'Bharatiya Janta Party',
      image: modiImg,
      description:
        'Narendra Modi is the current Prime Minister of India, known for his strong leadership, focus on national security, infrastructure growth, and diplomacy.',
      manifesto: 'https://www.bjp.org/bjp-manifesto-2024',
    },
    {
      name: 'Rahul Gandhi',
      party: 'Indian National Congress',
      image: rahulImg,
      description:
        'Rahul Gandhi advocates for social justice, inclusive growth, and democratic values. He envisions a progressive India built on equality and sustainability.',
      manifesto: 'https://inc.in/',
    },
    {
      name: 'Arvind Kejriwal',
      party: 'Aam Aadmi Party',
      image: kejriwalImg,
      description:
        'Arvind Kejriwal focuses on clean governance, education and healthcare reforms. He champions transparency and people-centric policies.',
      manifesto: 'https://aamaadmiparty.org/',
    },
    {
      name: 'Asaduddin Owaisi',
      party: 'AIMIM',
      image: owaisiImg,
      description:
        'Asaduddin Owaisi is a strong advocate for minority rights and constitutional values. He focuses on education, legal awareness, and representation.',
      manifesto: 'https://www.aimim.org/',
    },
  ];

  return (
    <div className="candidate-list-container">
      <h2 className="candidate-list-title">üó≥Ô∏è Candidates Contesting the Election</h2>
      <div className="candidate-grid">
        {candidates.map((cand, index) => (
          <div key={index} className="candidate-card">
            <img src={cand.image} alt={cand.name} className="candidate-img" />
            <h3>{cand.name}</h3>
            <p><strong>Party:</strong> {cand.party}</p>
            <p className="candidate-desc">{cand.description}</p>
            <a href={cand.manifesto} target="_blank" rel="noopener noreferrer" className="manifesto-link">
              View Manifesto ‚Üí
            </a>
          </div>
        ))}
      </div>
    </div>
  );
}

export default CandidateList;
