import { useEffect, useState } from 'react';
import { Link } from 'react-router-dom';
import './Home.css'; // Make sure this file exists for styling

function Home() {
  const [candidates, setCandidates] = useState([]);
  const [electionDate, setElectionDate] = useState('');

  useEffect(() => {
    // Static data using image URLs directly
    setCandidates([
      {
        name: 'Narendra Modi',
        party: 'BJP',
        image: 'https://upload.wikimedia.org/wikipedia/commons/1/1e/Narendra_Modi_2021.jpg',
      },
      {
        name: 'Rahul Gandhi',
        party: 'Congress',
        image: 'https://upload.wikimedia.org/wikipedia/commons/3/3e/Rahul_Gandhi_2023.jpg',
      },
      {
        name: 'Arvind Kejriwal',
        party: 'AAP',
        image: 'https://upload.wikimedia.org/wikipedia/commons/1/1b/Arvind_Kejriwal_2022.jpg',
      },
      {
        name: 'Asaduddin Owaisi',
        party: 'AIMIM',
        image: 'https://upload.wikimedia.org/wikipedia/commons/1/13/Asaduddin_Owaisi.jpg',
      }
    ]);

    setElectionDate('July 25, 2025');
  }, []);

  return (
    <div className="home-container">
      <h1 className="home-title">üó≥Ô∏è Welcome to VoteHub</h1>

      <blockquote className="home-quote">
        "The vote is the most powerful nonviolent tool we have." ‚Äì John Lewis
      </blockquote>

      <h2 className="home-date">üìÖ Election Date: {electionDate}</h2>

      <h3 className="home-subheading">Candidates:</h3>
      <div className="candidate-list">
        {candidates.map((cand, index) => (
          <div key={index} className="candidate-card">
            <img src={cand.image} alt={cand.name} className="candidate-img" />
            <p className="candidate-name">{cand.name}</p>
            <p className="candidate-party">{cand.party}</p>
          </div>
        ))}
      </div>

      <div className="home-buttons">
        <Link to="/register">
          <button className="register-btn">Register</button>
        </Link>
        <Link to="/login">
          <button className="login-btn">Login</button>
        </Link>
      </div>
    </div>
  );
}

export default Home;
