import { useEffect, useState } from 'react';
import { Link } from 'react-router-dom';
import './Home.css';

// Candidate Images
import modiImg from '../assets/candidates/narendra_modi.jpg';
import rahulImg from '../assets/candidates/Rahul_Gandhi.jpg';
import kejriwalImg from '../assets/candidates/Arvind_Kejriwal.jpg';
import owaisiImg from '../assets/candidates/Asaduddin_Owaisi.jpg';

// Emblem Image
import emblem from '../assets/candidates/emblem.jpg';

// Auth utils
import { isLoggedIn, logout } from '../utils/authUtils';

function Home() {
  const [candidates, setCandidates] = useState([]);
  const [electionDate, setElectionDate] = useState('');

  useEffect(() => {
    setCandidates([
      {
        name: 'Narendra Modi',
        party: 'Bharatiya Janta Party',
        image: modiImg,
        description:
          'Narendra Modi is the current Prime Minister of India, known for his strong leadership, emphasis on development, and focus on national security, infrastructure growth, and international diplomacy.',
        manifesto: 'https://www.bjp.org/bjp-manifesto-2024',
      },
      {
        name: 'Rahul Gandhi',
        party: 'Indian National Congress',
        image: rahulImg,
        description:
          'Rahul Gandhi, a prominent leader of the Indian National Congress, advocates for social justice, inclusive growth, and democratic values. Committed to empowering youth and marginalized communities, he envisions a progressive India built on equality, transparency, and sustainable development.',
        manifesto: 'https://inc.in/',
      },
      {
        name: 'Arvind Kejriwal',
        party: 'Aam Aadmi Party',
        image: kejriwalImg,
        description:
          'Arvind Kejriwal, founder of the Aam Aadmi Party, is known for his focus on clean governance, education, and healthcare reforms. A former civil servant, he champions transparency, anti-corruption, and people-centric policies aimed at empowering the common citizen.',
        manifesto: 'https://aamaadmiparty.org/',
      },
      {
        name: 'Asaduddin Owaisi',
        party: 'AIMIM',
        image: owaisiImg,
        description:
          'Asaduddin Owaisi, leader of the All India Majlis-e-Ittehadul Muslimeen (AIMIM), is a strong advocate for minority rights, constitutional values, and social justice. Known for his articulate speeches, he focuses on empowering marginalized communities through education, legal awareness, and political representation.',
        manifesto: 'https://www.aimim.org/',
      },
    ]);

    setElectionDate('July 25, 2025');
  }, []);

  return (
    <div className="home-container">
      <header className="home-header">
        <img src={emblem} alt="Indian Emblem" className="emblem" />
        <h1 className="home-title">üó≥Ô∏è VoteYatra</h1>

        <div className="header-buttons">
          {!isLoggedIn() ? (
            <>
              <Link to="/register">
                <button className="register-header-btn">Register for the upcoming election</button>
              </Link>
              <Link to="/login">
                <button className="login-header-btn">Already registered? Log in</button>
              </Link>
            </>
          ) : (
            <button onClick={logout} className="login-header-btn">Logout</button>
          )}
        </div>
      </header>

      <hr className="separator" />

      <p className="voting-info">
        Voting is not just a right ‚Äî it is the heartbeat of a democracy. It gives every citizen the
        power to influence the leadership, policies, and direction of the country. When you vote,
        you‚Äôre not only choosing a candidate, but shaping the future of your community and nation.
        However, despite its importance, voter awareness and participation often remain low due to
        lack of clarity, accessibility, or trust in the process. That‚Äôs where <strong>VoteYatra</strong> steps in ‚Äî a
        secure, transparent, and user-friendly platform designed to simplify and encourage
        participation in the democratic process. With VoteYatra, citizens can stay informed about
        election dates, explore verified candidate details, and prepare to cast their vote with
        confidence. Our mission is to make voting easier, more engaging, and accessible to all.
        Because democracy only works when we all show up. Your vote. Your voice. Your journey. Let
        VoteYatra be the bridge to your ballot.
      </p>

      <div className="main-content">
        <h2 className="home-date">üìÖ Election Date: {electionDate}</h2>

        <h3 className="home-subheading">Candidates:</h3>
        <div className="candidate-vertical-list">
          {candidates.map((cand, index) => (
            <div key={index} className="candidate-vertical-card">
              <img src={cand.image} alt={cand.name} className="candidate-img-vertical" />
              <div className="candidate-details">
                <p className="candidate-name">{cand.name}</p>
                <p className="candidate-party">{cand.party}</p>
                <p className="candidate-desc">{cand.description}</p>
                <a
                  href={cand.manifesto}
                  target="_blank"
                  rel="noopener noreferrer"
                  className="manifesto-link"
                >
                  View Manifesto ‚Üí
                </a>
              </div>
            </div>
          ))}
        </div>
      </div>
    </div>
  );
}

export default Home;
