// src/pages/Admin.jsx
import React, { useEffect, useState } from 'react';
import axios from 'axios';
import './Admin.css';

function Admin() {
  const [votes, setVotes] = useState([]);
  const [summary, setSummary] = useState([]);
  const [users, setUsers] = useState([]);
  const [aadhaarToDelete, setAadhaarToDelete] = useState('');
  const [aadhaarToEmail, setAadhaarToEmail] = useState('');
  const [deleteStatus, setDeleteStatus] = useState('');
  const [emailSubject, setEmailSubject] = useState('');
  const [emailMessage, setEmailMessage] = useState('');
  const [emailStatus, setEmailStatus] = useState('');
  

  const token = localStorage.getItem('voteyatra_token');

  const fetchVotes = async () => {
    try {
      const res = await axios.get('http://localhost:3000/vote/admin/all-votes', {
        headers: { Authorization: `Bearer ${token}` },
      });
      setVotes(res.data);
    } catch (err) {
      console.error('Error fetching votes:', err);
    }
  };

  const fetchSummary = async () => {
    try {
      const res = await axios.get('http://localhost:3000/vote/admin/vote-summary', {
        headers: { Authorization: `Bearer ${token}` },
      });
      setSummary(res.data);
    } catch (err) {
      console.error('Error fetching summary:', err);
    }
  };

  const fetchUsers = async () => {
    try {
      const res = await axios.get('http://localhost:3000/vote/admin/all-users', {
        headers: { Authorization: `Bearer ${token}` },
      });
      setUsers(res.data);
    } catch (err) {
      console.error('Error fetching users:', err);
    }
  };

  const deleteUser = async () => {
    try {
      const res = await axios.delete(`http://localhost:3000/vote/admin/delete-user/${aadhaarToDelete}`, {
        headers: { Authorization: `Bearer ${token}` },
      });
      setDeleteStatus(res.data.message);
      fetchUsers();
    } catch (err) {
      const msg = err.response?.data?.message || 'Failed to delete user';
setDeleteStatus(msg);
alert(msg); // üö® Show popup

    }
  };

  return (
    <div className="admin-container">
      <h2>üîê Admin Portal</h2>

      <div className="admin-actions">
        <button onClick={fetchVotes}>üìã View All Votes</button>
        <button onClick={fetchSummary}>üìä View Vote Summary</button>
        <button onClick={fetchUsers}>üë• View All Users</button>
      </div>

      <div className="admin-delete">
        <input
  type="text"
  placeholder="Enter Aadhaar to delete user"
  value={aadhaarToDelete}
  onChange={(e) => setAadhaarToDelete(e.target.value)}
/>

        <button onClick={deleteUser}>üóëÔ∏è Delete User</button>
        {deleteStatus && <p className="status">{deleteStatus}</p>}
      </div>

      <div className="admin-section">
        {votes.length > 0 && (
          <div>
            <h3>All Votes</h3>
            {votes.map((vote, i) => (
              <div key={i}>{vote.userId?.username} ‚Üí {vote.choice}</div>
            ))}
          </div>
        )}

        {summary.length > 0 && (
          <div>
            <h3>Vote Summary</h3>
            {summary.map((item, i) => (
              <div key={i}>{item._id} - {item.count} votes</div>
            ))}
          </div>
        )}

        {users.length > 0 && (
  <div>
    <h3>All Users</h3>
    {users.map((user, i) => (
      <div key={i}>
        <strong>{user.username}</strong> | {user.email} | Aadhaar: {user.aadhaar} | Role: {user.role}
      </div>
    ))}
  </div>
)}  

      </div>
    <div className="admin-email">
  <h4>üìß Send Email to Specific User</h4>
  <input
  type="text"
  placeholder="Enter Aadhaar"
  value={aadhaarToEmail}
  onChange={(e) => setAadhaarToEmail(e.target.value)}
/>

  <input
    type="text"
    placeholder="Subject"
    value={emailSubject}
    onChange={(e) => setEmailSubject(e.target.value)}
  />
  <textarea
    placeholder="Message"
    value={emailMessage}
    onChange={(e) => setEmailMessage(e.target.value)}
  />
  <button
    onClick={async () => {
      try {
        const res = await axios.post(
          `http://localhost:3000/vote/admin/send-mail/$${aadhaarToEmail}`,
          { subject: emailSubject, message: emailMessage },
          { headers: { Authorization: `Bearer ${token}` } }
        );
        setEmailStatus(res.data.message);
      } catch (err) {
        setEmailStatus(err.response?.data?.message || 'Failed to send email');
      }
    }}
  >
    ‚úâÔ∏è Send Email to User
  </button>
</div>
    <div className="admin-email">
  <h4>üìß Send Email to All Users</h4>
  <input
    type="text"
    placeholder="Subject"
    value={emailSubject}
    onChange={(e) => setEmailSubject(e.target.value)}
  />
  <textarea
    placeholder="Message"
    value={emailMessage}
    onChange={(e) => setEmailMessage(e.target.value)}
  />
  <button
    onClick={async () => {
      try {
        const res = await axios.post(
          `http://localhost:3000/vote/admin/send-mail/all`,
          { subject: emailSubject, message: emailMessage },
          { headers: { Authorization: `Bearer ${token}` } }
        );
        setEmailStatus(res.data.message);
      } catch (err) {
        setEmailStatus(err.response?.data?.message || 'Failed to send email');
      }
    }}
  >
    ‚úâÔ∏è Send Email to All Users
  </button>
  {emailStatus && <p className="status">{emailStatus}</p>}
</div>

    </div>
  );
}

export default Admin;
