import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { useLocation, useNavigate } from 'react-router-dom';
import './CompleteRegistration.css';

function CompleteRegistration() {
  const navigate = useNavigate();
  const location = useLocation();
  const passedEmail = location.state?.email || '';

  const [formData, setFormData] = useState({
    username: '',
    password: '',
    email: '',
    aadhaar: '',
  });

  // ✅ Set email only once on page load
  useEffect(() => {
    if (!passedEmail) {
      alert('No email found. Please start from the Register page.');
      navigate('/register');
    } else {
      setFormData((prev) => ({ ...prev, email: passedEmail }));
    }
  }, [passedEmail, navigate]);

  const handleChange = (e) => {
    const { name, value } = e.target;

    if (name === 'aadhaar') {
      const raw = value.replace(/\D/g, '');
      const formatted = raw.match(/.{1,4}/g)?.join(' ').slice(0, 14);
      setFormData({ ...formData, [name]: formatted || '' });
    } else {
      setFormData({ ...formData, [name]: value });
    }
  };

  const handleSubmit = async (e) => {
    e.preventDefault();

    const aadhaar = formData.aadhaar.trim();
    const aadhaarRegex = /^\d{4} \d{4} \d{4}$/;

    if (!aadhaarRegex.test(aadhaar)) {
      alert('Aadhar must be in format: 1234 5678 9123');
      return;
    }

    try {
      await axios.post('http://localhost:3000/auth/register', {
        username: formData.username,
        password: formData.password,
        email: formData.email,
        aadhaar,
      });
      alert('Registration successful!');
      navigate('/login');
    } catch (err) {
      alert(err.response?.data?.message || 'Error registering user');
    }
  };

  return (
    <div className="complete-registration">
      <h2>Complete Your Registration</h2>
      <form onSubmit={handleSubmit}>
        <input
          type="text"
          name="username"
          placeholder="Candidate Name"
          value={formData.username}
          onChange={handleChange}
          required
        />
        <input
          type="password"
          name="password"
          placeholder="Password"
          value={formData.password}
          onChange={handleChange}
          required
        />
        <input
          type="email"
          name="email"
          placeholder="Email"
          value={formData.email}
          disabled // ✅ prevent editing
        />
        <input
          type="text"
          name="aadhaar"
          placeholder="Aadhaar Number"
          value={formData.aadhaar}
          onChange={handleChange}
          maxLength={14}
          pattern="[0-9]{4}\s[0-9]{4}\s[0-9]{4}"
          title="Format: 1234 5678 9123"
          required
        />
        <button type="submit">Register</button>
      </form>
    </div>
  );
}

export default CompleteRegistration;
