import React, { useState } from 'react';
import axios from 'axios';
import { useLocation, useNavigate } from 'react-router-dom';
import './CompleteRegistration.css';

function CompleteRegistration() {
  const navigate = useNavigate();
  const location = useLocation();
  const email = location.state?.email;

  const [formData, setFormData] = useState({
    fullName: '',
    username: '',
    password: '',
    aadharNumber: '',
  });

  const handleChange = (e) => {
  const { name, value } = e.target;

  if (name === 'aadharNumber') {
    // Remove all non-digits
    const raw = value.replace(/\D/g, '');

    // Group into 4-4-4 format with spaces
    const formatted = raw
      .match(/.{1,4}/g)   // split into groups of max 4 digits
      ?.join(' ')         // join with space
      .slice(0, 14);      // limit total length

    setFormData({ ...formData, [name]: formatted || '' });
  } else {
    setFormData({ ...formData, [name]: value });
  }
};


  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      await axios.post('http://localhost:3000/auth/register', {
        email,
        ...formData,
      });
      alert('Registration successful!');
      navigate('/login');
    } catch (err) {
      alert(err.response?.data?.message || 'Error registering user');
    }
  };

  return (
    <div className="complete-registration">
      <h2>Complete Your Registration</h2>
      <form onSubmit={handleSubmit}>
        <input
          type="text"
          name="fullName"
          placeholder="Full Name"
          onChange={handleChange}
          required
        />
        <input
          type="text"
          name="username"
          placeholder="Username"
          onChange={handleChange}
          required
        />
        <input
          type="password"
          name="password"
          placeholder="Password"
          onChange={handleChange}
          required
        />
        <input
  type="text"
  name="aadharNumber"
  placeholder="Aadhar Number"
  value={formData.aadharNumber}
  onChange={handleChange}
  maxLength={14}
  pattern="[0-9]{4}\s[0-9]{4}\s[0-9]{4}"
  title="Format: 1234 5678 9123"
  required
/>



        <button type="submit">Register</button>
      </form>
    </div>
  );
}

export default CompleteRegistration;
