import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import './BookSlot.css';

function BookSlot() {
  const [selectedSlot, setSelectedSlot] = useState('');
  const [message, setMessage] = useState('');
  const navigate = useNavigate();

  const slots = [
    '9:00 AM - 10:00 AM',
    '10:00 AM - 11:00 AM',
    '11:00 AM - 12:00 PM',
    '12:00 PM - 1:00 PM',
    '2:00 PM - 3:00 PM',
    '3:00 PM - 4:00 PM',
  ];

  useEffect(() => {
    const user = localStorage.getItem('voteyatra_user');
    if (!user) {
      alert('Please log in to book a slot.');
      navigate('/login', { replace: true }); 
    }
  }, [navigate]);

  const handleSubmit = (e) => {
    e.preventDefault();
    if (!selectedSlot) {
      setMessage('Please select a time slot.');
      return;
    }

    // In a real app, send slot info to the backend
    setMessage(`âœ… Slot booked successfully: ${selectedSlot}`);
  };

  return (
    <div className="slot-container">
      <h2>ðŸ“… Book Your Voting Slot</h2>
      <form onSubmit={handleSubmit}>
        <label>Select a time slot:</label>
        <select value={selectedSlot} onChange={(e) => setSelectedSlot(e.target.value)}>
          <option value="">-- Select Slot --</option>
          {slots.map((slot, idx) => (
            <option key={idx} value={slot}>
              {slot}
            </option>
          ))}
        </select>
        <button type="submit">Book Slot</button>
      </form>
      {message && <p className="slot-message">{message}</p>}
    </div>
  );
}

export default BookSlot;
