import { useEffect, useState } from 'react';
import { Link } from 'react-router-dom';
import axios from 'axios';

function Home() {
  const [candidates, setCandidates] = useState([]);
  const [electionDate, setElectionDate] = useState('Coming soon...');

  useEffect(() => {
    // Fetch candidates and date (static for now, dynamic later)
    async function fetchHomeData() {
      try {
        const res = await axios.get('http://localhost:5000/api/candidates'); // optional
        setCandidates(res.data.candidates || []); // assuming API shape
        setElectionDate(res.data.electionDate || 'July 25, 2025'); // fallback value
      } catch (err) {
        console.error('Failed to fetch candidates or election date');
        setCandidates([
          { name: 'Candidate A', party: 'Party X' },
          { name: 'Candidate B', party: 'Party Y' },
        ]);
        setElectionDate('July 25, 2025');
      }
    }

    fetchHomeData();
  }, []);

  return (
    <div className="p-6 max-w-4xl mx-auto text-center">
      <h1 className="text-4xl font-bold mb-4">üó≥Ô∏è Welcome to VoteHub</h1>

      <blockquote className="italic text-lg text-gray-700 mb-6">
        "The vote is the most powerful nonviolent tool we have." ‚Äì John Lewis
      </blockquote>

      <h2 className="text-2xl font-semibold mb-2">Upcoming Election Date:</h2>
      <p className="text-xl text-red-600 mb-6">{electionDate}</p>

      <h2 className="text-2xl font-semibold mb-4">Candidates:</h2>
      <ul className="mb-6">
        {candidates.map((cand, index) => (
          <li key={index} className="text-lg">
            üßë {cand.name} ‚Äì <span className="text-blue-600">{cand.party}</span>
          </li>
        ))}
      </ul>

      <div className="flex justify-center gap-4 mt-8">
        <Link to="/register">
          <button className="bg-green-600 text-white px-4 py-2 rounded">Register</button>
        </Link>
        <Link to="/login">
          <button className="bg-blue-600 text-white px-4 py-2 rounded">Login</button>
        </Link>
      </div>
    </div>
  );
}

export default Home;
