import { useEffect, useState } from 'react';
import { Link } from 'react-router-dom';
import './Home.css';

// Candidate Images
import modiImg from '../assets/candidates/narendra_modi.jpg';
import rahulImg from '../assets/candidates/Rahul_Gandhi.jpg';
import kejriwalImg from '../assets/candidates/Arvind_Kejriwal.jpg';
import owaisiImg from '../assets/candidates/Asaduddin_Owaisi.jpg';

// Emblem Image
import emblem from '../assets/candidates/emblem.jpg';

function Home() {
  const [candidates, setCandidates] = useState([]);
  const [electionDate, setElectionDate] = useState('');

  useEffect(() => {
    setCandidates([
      {
        name: 'Narendra Modi',
        party: 'Bharatiy ',
        image: modiImg,
        description: 'Narendra Modi is the current Prime Minister of India, known for his strong leadership, emphasis on development, and focus on national security, infrastructure growth, and international diplomacy.',
        manifesto: 'https://www.bjp.org/en/manifesto2024',
      },
      {
        name: 'Rahul Gandhi',
        party: 'Congress',
        image: rahulImg,
        description: 'Rahul Gandhi, a prominent leader of the Indian National Congress, advocates for social justice, inclusive growth, and democratic values. Committed to empowering youth and marginalized communities, he envisions a progressive India built on equality, transparency, and sustainable development.',
        manifesto: 'https://inc.in/en/manifesto',
      },
      {
        name: 'Arvind Kejriwal',
        party: 'AAP',
        image: kejriwalImg,
        description: 'Arvind Kejriwal, founder of the Aam Aadmi Party, is known for his focus on clean governance, education, and healthcare reforms. A former civil servant, he champions transparency, anti-corruption, and people-centric policies aimed at empowering the common citizen.',
        manifesto: 'https://aamaadmiparty.org/manifesto',
      },
      {
        name: 'Asaduddin Owaisi',
        party: 'AIMIM',
        image: owaisiImg,
        description: 'Asaduddin Owaisi, leader of the All India Majlis-e-Ittehadul Muslimeen (AIMIM), is a strong advocate for minority rights, constitutional values, and social justice. Known for his articulate speeches, he focuses on empowering marginalized communities through education, legal awareness, and political representation.',
        manifesto: 'https://aimim.in/manifesto',
      },
    ]);
    setElectionDate('July 25, 2025');
  }, []);

  return (
    <div className="home-container">
      <header className="home-header">
        <img src={emblem} alt="Indian Emblem" className="emblem" />
        <h1 className="home-title">üó≥Ô∏è Welcome to VoteHub</h1>
        <Link to="/register">
          <button className="register-header-btn">Register for the upcoming election</button>
        </Link>
      </header>

      <hr className="separator" />

      <div className="main-content">
        <h2 className="home-date">üìÖ Election Date: {electionDate}</h2>

        <h3 className="home-subheading">Candidates:</h3>
        <div className="candidate-vertical-list">
          {candidates.map((cand, index) => (
            <div key={index} className="candidate-vertical-card">
              <img src={cand.image} alt={cand.name} className="candidate-img-vertical" />
              <div className="candidate-details">
                <p className="candidate-name">{cand.name}</p>
                <p className="candidate-party">{cand.party}</p>
                <p className="candidate-desc">{cand.description}</p>
                <a
                  href={cand.manifesto}
                  target="_blank"
                  rel="noopener noreferrer"
                  className="manifesto-link"
                >
                  View Manifesto ‚Üí
                </a>
              </div>
            </div>
          ))}
        </div>
      </div>
    </div>
  );
}

export default Home;
