import { useEffect, useState } from 'react';
import { Link } from 'react-router-dom';
import axios from 'axios';
import './Home.css'; // Assuming you're using the CSS file we created earlier

function Home() {
  const [candidates, setCandidates] = useState([]);
  const [electionDate, setElectionDate] = useState('Coming soon...');

  useEffect(() => {
    async function fetchData() {
      try {
        const res = await axios.get('http://localhost:5000/api/candidates');
        setCandidates(res.data.candidates || []);
        setElectionDate(res.data.electionDate || 'July 25, 2025');
      } catch (err) {
        // Static fallback data (real candidates)
        setCandidates([
          { name: 'Narendra Modi', party: 'BJP' },
          { name: 'Rahul Gandhi', party: 'Congress' },
          { name: 'Arvind Kejriwal', party: 'AAP' },
          { name: 'Asaduddin Owaisi', party: 'AIMIM' }
        ]);
        setElectionDate('July 25, 2025');
      }
    }

    fetchData();
  }, []);

  return (
    <div className="home-container">
      <h1 className="home-title">üó≥Ô∏è Welcome to VoteHub</h1>

      <blockquote className="home-quote">
        "The vote is the most powerful nonviolent tool we have." ‚Äì John Lewis
      </blockquote>

      <h2 className="home-date">üìÖ Election Date: {electionDate}</h2>

      <h3 className="home-subheading">Candidates:</h3>
      <ul>
        {candidates.map((cand, index) => (
          <li key={index} className="home-candidate">
            üßë {cand.name} ‚Äì <span>{cand.party}</span>
          </li>
        ))}
      </ul>

      <div className="home-buttons">
        <Link to="/register">
          <button className="register-btn">Register</button>
        </Link>
        <Link to="/login">
          <button className="login-btn">Login</button>
        </Link>
      </div>
    </div>
  );
}

export default Home;
