import React, { useState } from 'react';
import axios from 'axios';
import { useLocation, useNavigate } from 'react-router-dom';
import './CompleteRegistration.css';

function CompleteRegistration() {
  const navigate = useNavigate();
  const location = useLocation();
  const email = location.state?.email;

  const [formData, setFormData] = useState({
    fullName: '',
    username: '',
    password: '',
    aadhaar: '',
  });

  const handleChange = (e) => {
  const { name, value } = e.target;

  if (name === 'aadhaar') {
    const raw = value.replace(/\D/g, ''); // Remove non-digits
    const formatted = raw.match(/.{1,4}/g)?.join(' ').slice(0, 14); // Format 4 4 4
    setFormData({ ...formData, [name]: formatted || '' });
  } else {
    setFormData({ ...formData, [name]: value });
  }
};




  const handleSubmit = async (e) => {
  e.preventDefault();

  const aadhaar = formData.aadhaar.trim();
  const aadhaarRegex = /^\d{4} \d{4} \d{4}$/;

  if (!aadhaarRegex.test(aadhaar)) {
    alert('Aadhar must be in format: 1234 5678 9123');
    return;
  }

  try {
    await axios.post('http://localhost:3000/auth/register', {
      email,
      ...formData,
      aadhaar,
    });
    alert('Registration successful!');
    navigate('/login');
  } catch (err) {
    alert(err.response?.data?.message || 'Error registering user');
  }
};



  return (
    <div className="complete-registration">
      <h2>Complete Your Registration</h2>
      <form onSubmit={handleSubmit}>
        <input
          type="text"
          name="fullName"
          placeholder="Full Name"
          onChange={handleChange}
          required
        />
        <input
          type="text"
          name="username"
          placeholder="Username"
          onChange={handleChange}
          required
        />
        <input
          type="password"
          name="password"
          placeholder="Password"
          onChange={handleChange}
          required
        />
        <input
  type="text"
  name="aadhaar"
  placeholder="Aadhaar Number"
  value={formData.aadhaar}
  onChange={handleChange}
  maxLength={14}
  pattern="[0-9]{4}\s[0-9]{4}\s[0-9]{4}"
  title="Format: 1234 5678 9123"
  required
/>





        <button type="submit">Register</button>
      </form>
    </div>
  );
}

export default CompleteRegistration;
