import React, { useEffect, useState } from 'react';
import axios from 'axios';
import './CastVote.css';
import { Link, useNavigate } from 'react-router-dom';
import emblem from '../assets/candidates/emblem.jpg';

function CastVote() {
  const [candidates, setCandidates] = useState([]);
  const [selectedCandidate, setSelectedCandidate] = useState('');
  const [message, setMessage] = useState('');
  const [loggedInUser, setLoggedInUser] = useState(null);
  const navigate = useNavigate();

  const token = localStorage.getItem('voteyatra_token');
  const role = localStorage.getItem('voteyatra_role');
  const user = localStorage.getItem('voteyatra_user');

  useEffect(() => {
    // üßæ Validate access
    if (!token || !user) {
      sessionStorage.setItem('flashMessage', 'Please log in to access Cast Vote');
      navigate('/login');
      return;
    }

    if (role === 'admin') {
      alert('Admins are not allowed to vote.');
      navigate('/');
      return;
    }

    setLoggedInUser(user);
    fetchCandidates();
  }, [navigate, token, role]);

  const fetchCandidates = async () => {
    try {
      const res = await axios.get('http://localhost:3000/vote/candidates', {
        headers: {
          Authorization: `Bearer ${token}`,
        },
      });
      setCandidates(res.data);
    } catch (err) {
      setMessage(err.response?.data?.message || 'Failed to fetch candidates');
    }
  };

  const castVote = async () => {
    if (!selectedCandidate) return alert('Please select a candidate');

    try {
      const res = await axios.post(
        'http://localhost:3000/vote/cast-vote',
        { choice: selectedCandidate },
        {
          headers: {
            Authorization: `Bearer ${token}`,
          },
        }
      );
      alert(res.data.message);
      navigate('/');
    } catch (err) {
      alert(err.response?.data?.message || 'Failed to cast vote');
    }
  };

  const handleLogout = () => {
    localStorage.removeItem('voteyatra_user');
    localStorage.removeItem('voteyatra_token');
    localStorage.removeItem('voteyatra_role');
    window.location.href = '/'; // üîÅ Full reload to clear memory
  };

  return (
    <>
      <header className="home-header4">
        <img src={emblem} alt="Indian Emblem" className="emblem" />
        <Link to="/" className="no-underline">
          <h1 className="home-title">üó≥Ô∏è VoteYatra</h1>
        </Link>

        <div className="header-buttons">
          {loggedInUser ? (
            <>
              <span className="welcome-user">Welcome, {loggedInUser}</span>
              <button className="logout-btn" onClick={handleLogout}>
                Logout
              </button>
            </>
          ) : (
            <>
              <Link to="/register">
                <button className="register-header-btn">Register</button>
              </Link>
              <Link to="/login">
                <button className="login-header-btn">Log in</button>
              </Link>
            </>
          )}
        </div>
      </header>

      <hr className="separator" />

      <div className="cast-vote-container">
        <h2>üó≥Ô∏è Cast Your Vote</h2>
        {message && <p className="error">{message}</p>}

        <div className="candidates-list">
          {candidates.map((candidate) => (
            <div
              key={candidate._id}
              className={`candidate-card ${
                selectedCandidate === candidate.name ? 'selected' : ''
              }`}
              onClick={() => setSelectedCandidate(candidate.name)}
            >
              <img src={candidate.symbol} alt={candidate.name} />
              <h4>{candidate.name}</h4>
              <p>{candidate.party}</p>
            </div>
          ))}
        </div>

        <button className="submit-vote-btn" onClick={castVote}>
          Submit Vote
        </button>
      </div>
    </>
  );
}

export default CastVote;
