import React, { useState } from 'react';
import axios from 'axios';
import './BookSlot.css';


const slotMap = {
  1: '9:00 AM - 10:00 AM',
  2: '10:00 AM - 11:00 AM',
  3: '11:00 AM - 12:00 PM',
  4: '12:00 PM - 1:00 PM',
  5: '1:00 PM - 2:00 PM',
  6: '2:00 PM - 3:00 PM',
  7: '3:00 PM - 4:00 PM',
  8: '4:00 PM - 5:00 PM',
  9: '5:00 PM - 6:00 PM',
};

function BookSlot() {
  const [selectedSlot, setSelectedSlot] = useState('');
  const [message, setMessage] = useState('');

  const handleSlotChange = (e) => {
    setSelectedSlot(e.target.value);
  };

  const handleSubmit = async (e) => {
    e.preventDefault();

    const token = localStorage.getItem('voteyatra_token');
    if (!token) {
      alert('You must be logged in to book a slot');
      return;
    }

    try {
      const res = await axios.post(
        'http://localhost:3000/vote/book-slot',
        { slot: selectedSlot },
        {
          headers: {
            Authorization: `Bearer ${token}`,
          },
        }
      );
      setMessage(res.data.message);
    } catch (err) {
      setMessage(err.response?.data?.message || 'Failed to book slot');
    }
  };

  return (
    
    <div className="book-slot-container">
      <h2>ðŸ“… Book Your Voting Slot</h2>
      <form onSubmit={handleSubmit} className="slot-form">
        {Object.entries(slotMap).map(([slotNum, label]) => (
          <label key={slotNum} className="slot-option">
            <input
              type="radio"
              name="slot"
              value={slotNum}
              checked={selectedSlot === slotNum}
              onChange={handleSlotChange}
              required
            />
            {label}
          </label>
        ))}
        <button type="submit" className="book-btn">Book Slot</button>
      </form>
      {message && <p className="status-message">{message}</p>}
    </div>
  );
}

export default BookSlot;
