import React, { useState, useEffect } from 'react';
import axios from 'axios';
import './BookSlot.css';

const generateSlots = () => {
  const slots = [];
  for (let hour = 9; hour < 18; hour++) {
    const start = `${hour.toString().padStart(2, '0')}:00`;
    const end = `${(hour + 1).toString().padStart(2, '0')}:00`;
    slots.push({ start, end });
  }
  return slots;
};

function BookSlot() {
  const [availableSlots, setAvailableSlots] = useState([]);
  const [message, setMessage] = useState('');
  const [selectedSlot, setSelectedSlot] = useState(null);

  useEffect(() => {
    const token = localStorage.getItem('voteyatra_token');
    if (!token) {
      alert('You must be logged in to view slots');
      return;
    }

    // Fetch available slots from backend (optional enhancement)
    // If not supported by backend, fallback to all slots
    setAvailableSlots(generateSlots());
  }, []);

  const handleBooking = async (slot) => {
    const token = localStorage.getItem('voteyatra_token');
    if (!token) {
      alert('Please log in to book a slot');
      return;
    }

    try {
      const res = await axios.post(
        'http://localhost:3000/vote/book-slot',
        {
          start: `2025-07-25T${slot.start}:00`,
          end: `2025-07-25T${slot.end}:00`,
        },
        {
          headers: { Authorization: `Bearer ${token}` },
        }
      );
      setMessage(res.data.message);
      setSelectedSlot(`${slot.start} - ${slot.end}`);
    } catch (err) {
      setMessage(err.response?.data?.message || 'Failed to book slot');
    }
  };

  return (
    <div className="book-slot-container">
      <h2>ðŸ“… Book Your Voting Slot</h2>
      <p className="slot-date">Election Day: <strong>July 25, 2025</strong></p>

      <div className="slots-grid">
        {availableSlots.map((slot, index) => (
          <button
            key={index}
            className={`slot-btn ${selectedSlot === `${slot.start} - ${slot.end}` ? 'booked' : ''}`}
            onClick={() => handleBooking(slot)}
          >
            {slot.start} - {slot.end}
          </button>
        ))}
      </div>

      {message && <p className="status">{message}</p>}
    </div>
  );
}

export default BookSlot;
