import { useEffect, useState } from 'react';
import { Link } from 'react-router-dom';
import './Home.css';

// Candidate Images
import modiImg from '../assets/candidates/narendra_modi.jpg';
import rahulImg from '../assets/candidates/rahul_gandhi.jpg';
import kejriwalImg from '../assets/candidates/arvind_kejriwal.jpg';
import owaisiImg from '../assets/candidates/asaduddin_owaisi.jpg';

// Emblem Image
import emblem from '../assets/emblem.png';

function Home() {
  const [candidates, setCandidates] = useState([]);
  const [electionDate, setElectionDate] = useState('');

  useEffect(() => {
    setCandidates([
      { name: 'Narendra Modi', party: 'BJP', image: modiImg },
      { name: 'Rahul Gandhi', party: 'Congress', image: rahulImg },
      { name: 'Arvind Kejriwal', party: 'AAP', image: kejriwalImg },
      { name: 'Asaduddin Owaisi', party: 'AIMIM', image: owaisiImg },
    ]);
    setElectionDate('July 25, 2025');
  }, []);

  return (
    <div className="home-container">
      <header className="home-header">
        <img src={emblem} alt="Indian Emblem" className="emblem" />
        <h1 className="home-title">üó≥Ô∏è Welcome to VoteHub</h1>
        <Link to="/register">
          <button className="register-header-btn">Register for the upcoming election</button>
        </Link>
      </header>

      <hr className="separator" />

      <div className="main-content">
        <h2 className="home-date">üìÖ Election Date: {electionDate}</h2>

        <h3 className="home-subheading">Candidates:</h3>
        <div className="candidate-vertical-list">
          {candidates.map((cand, index) => (
            <div key={index} className="candidate-vertical-card">
              <img src={cand.image} alt={cand.name} className="candidate-img-vertical" />
              <div>
                <p className="candidate-name">{cand.name}</p>
                <p className="candidate-party">{cand.party}</p>
              </div>
            </div>
          ))}
        </div>
      </div>
    </div>
  );
}

export default Home;
