import React, { useEffect, useState } from 'react';
import { useNavigate } from 'react-router-dom';
import './CastVote.css';
import emblem from '../assets/candidates/emblem.jpg';
import modiImg from '../assets/candidates/narendra_modi.jpg';
import rahulImg from '../assets/candidates/Rahul_Gandhi.jpg';
import kejriwalImg from '../assets/candidates/Arvind_Kejriwal.jpg';
import owaisiImg from '../assets/candidates/Asaduddin_Owaisi.jpg';

const candidates = [
  { name: 'Narendra Modi', party: 'Bharatiya Janta Party', symbol: modiImg },
  { name: 'Rahul Gandhi', party: 'Indian National Congress', symbol: rahulImg },
  { name: 'Arvind Kejriwal', party: 'Aam Aadmi Party', symbol: kejriwalImg },
  { name: 'Asaduddin Owaisi', party: 'AIMIM', symbol: owaisiImg },
];

function CastVote() {
  const [selectedCandidate, setSelectedCandidate] = useState('');
  const [loggedInUser, setLoggedInUser] = useState(null);
  const [slot, setSlot] = useState(null);
  const [hasVoted, setHasVoted] = useState(false);
  const [timeLeft, setTimeLeft] = useState(null);
  const [canVote, setCanVote] = useState(false);
  const [message, setMessage] = useState('');
  const navigate = useNavigate();
  const token = localStorage.getItem('voteyatra_token');

  useEffect(() => {
    const user = localStorage.getItem('voteyatra_user');
    if (!token) {
      alert('Please log in to access Cast Vote');
      navigate('/', { replace: true });
    }
    setLoggedInUser(user);
    fetchUserSlot();
  }, []);

  useEffect(() => {
    if (slot && slot.start && slot.end) {
      const interval = setInterval(() => {
        const now = new Date();
        const start = new Date(slot.start);
        const end = new Date(slot.end);

        if (now >= start && now <= end) {
          setCanVote(true);
          setTimeLeft(null);
        } else if (now < start) {
          const diff = Math.max(0, start - now);
          setTimeLeft(Math.floor(diff / 1000));
        } else {
          setCanVote(false);
          setTimeLeft(null);
          setMessage('‚è≥ Your voting slot has ended. You missed your chance to vote.');
        }
      }, 1000);

      return () => clearInterval(interval);
    }
  }, [slot]);

  const fetchUserSlot = async () => {
    try {
      const res = await fetch('http://localhost:3000/auth/me', {
        headers: {
          Authorization: `Bearer ${token}`,
        },
      });
      const data = await res.json();
      if (!res.ok) throw new Error(data.message);
      if (!data.slot || !data.slot.start || !data.slot.end) {
        setMessage('‚ùå Please book a slot before you can vote.');
        return;
      }
      if (data.hasVoted) {
        setHasVoted(true);
        setMessage('‚úÖ You have already cast your vote. Thank you!');
        return;
      }
      setSlot(data.slot);
    } catch (err) {
      setMessage(err.message || 'Failed to fetch user slot info.');
    }
  };

  const castVote = async () => {
    if (!selectedCandidate) return alert('Please select a candidate');

    try {
      const res = await fetch('http://localhost:3000/vote/cast-vote', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
          Authorization: `Bearer ${token}`,
        },
        body: JSON.stringify({ choice: selectedCandidate }),
      });
      const data = await res.json();
      if (!res.ok) throw new Error(data.message);
      alert(data.message);
      navigate('/');
    } catch (err) {
      alert(err.message || 'Failed to cast vote');
    }
  };

  const formatTime = (seconds) => {
    const mins = Math.floor(seconds / 60);
    const secs = seconds % 60;
    return `${mins.toString().padStart(2, '0')}:${secs.toString().padStart(2, '0')}`;
  };

  return (
    <div className="cast-vote-page">
      <header className="home-header4">
        <img src={emblem} alt="Indian Emblem" className="emblem" />
        <h1 className="home-title">üó≥Ô∏è VoteYatra</h1>
        <div className="header-buttons">
          {loggedInUser && (
            <>
              <span className="welcome-user">Welcome, {loggedInUser}</span>
              <button
                className="logout-btn"
                onClick={() => {
                  localStorage.clear();
                  setLoggedInUser(null);
                  navigate('/');
                }}
              >Logout</button>
            </>
          )}
        </div>
      </header>

      <hr className="separator" />

      <div className="cast-vote-container">
        <h2>üó≥Ô∏è Cast Your Vote</h2>

        {message && <p className="error-msg">{message}</p>}

        {timeLeft !== null && !canVote && (
          <p className="timer">‚è≥ Time until your voting slot starts: {formatTime(timeLeft)}</p>
        )}

        {canVote && !hasVoted && (
          <>
            <div className="candidates-list">
              {candidates.map((candidate) => (
                <div
                  key={candidate.name}
                  className={`candidate-card ${
                    selectedCandidate === candidate.name ? 'selected' : ''
                  }`}
                  onClick={() => setSelectedCandidate(candidate.name)}
                >
                  <img src={candidate.symbol} alt={candidate.name} />
                  <h4>{candidate.name}</h4>
                  <p>{candidate.party}</p>
                </div>
              ))}
            </div>

            <button className="submit-vote-btn" onClick={castVote}>
              Submit Vote
            </button>
          </>
        )}
      </div>
    </div>
  );
}

export default CastVote;
